//Para cuando el mouse pase encima y se vaya
$("#textNombre").mouseover(function(){
    $(this).css({
        "background-color": "rgb(228, 228, 225)"
    });
});
$("#textNombre").mouseleave(function(){
    $(this).css({
        "background-color": "white"
    })
        ;
});

